import Link from 'next/link';
import Router from 'next/router';

const TicketIndex = ({ tickets }) => {
  const ticketList = tickets.map(ticket => {
    return(
      <tr key={ticket.id}>
        <td>{ticket.title}</td>
        <td>{ticket.price}</td>
        {orderStatus(order.status)}
        <td>
          <Link href="/tickets/edit/[ticketId]" as={`/tickets/edit/${ticket.id}`} >
            <a>Edit</a>
          </Link>
        </td>
      </tr>
    )
  })

  const orderStatus = (status)  => {
    if(status){
      return <td>Reserved</td>
    }
    return <td></td>
  }

  return (
    <div>
      <h1>My Tickets</h1>
      <button onClick={() => Router.push('/tickets/create')}>Sell New Ticket</button>
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Price</th>
            <th>Status</th>
            <th>Link</th>
          </tr>
        </thead>
        <tbody>
          {ticketList}
        </tbody>
      </table>
    </div>
  );

}

TicketIndex.getInitialProps = async (context, client) => {
  const { data } = await client.get('http://localhost:3002/api/tickets/myticket');

  return { tickets: data };
}

export default TicketIndex;
