import axios from 'axios';

export default ({ req }) => {
  if(typeof window === 'undefined') {
    return axios.create({
      baseURL: 'http://ingress-nginx.ingress-nginx.svc.cluster.local/api/users/currentuser',
      headers: req.headers
    });
  } else {
    return axios.create({
      baseURL: '/'
    })
  }
}
