import 'bootstrap/dist/css/bootstrap.css'
import axios from 'axios';
// import buildClient from '../api/build-client';
import Header from '../components/header';

const AppComponent = ({ Component, pageProps, currentUser }) => {
  return <div>
    <Header currentUser={currentUser}/>
    <Component {...pageProps} />
  </div>;
};

AppComponent.getInitialProps = async (appContext) => {
  let data = {};
  if(typeof window === 'undefined') {
    let { data } = await axios.get('http://localhost:3001/api/users/currentuser', {
      withCredentials: true, credentials: 'include', headers: appContext.ctx.req.headers
    });
    console.log('this is app component from server')
    console.log(data);
  } else {
    let { data } = await axios.get('http://localhost:3001/api/users/currentuser', {
      withCredentials: true, credentials: 'include'
    });
    console.log('this is app component from user')
    console.log(data);
  }

  let pageProps = {};
  if(appContext.Component.getInitialProps){
    pageProps = await appContext.Component.getInitialProps(appContext.ctx);
  }

  return { 
    pageProps, 
    currentUser: data.data.currentUser
  };
};

