const OrderIndex = ({ orders }) => {
  return (<div>
    <h1>My Order</h1>
    <ul>
      {orders.map(order => {
        return <li key={order.id}>{order.ticket.title} - {order.status}</li>
      })}
    </ul>
  </div>
  );

  return (
    <div>
      <h1>Tickets</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Ticket</th>
            <th>Price</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {orderList}
        </tbody>
      </table>
    </div>
  );

}

OrderIndex.getInitialProps = async (context, client) => {
  const { data } = await client.get('http://localhost:3003/api/orders');

  return { orders: data };
}

export default OrderIndex;
