import Link from 'next/link';
import Router from 'next/router';

const TicketIndex = ({ tickets }) => {
  const ticketList = tickets.map(ticket => {
    return(
      <tr key={ticket.id}>
        <td>{ticket.title}</td>
        <td>{ticket.price}</td>
        <td>
          <Link href="/tickets/[ticketId]" as={`/tickets/${ticket.id}`} >
            <a>View</a>
          </Link>
        </td>
      </tr>
    )
  })

  return (
    <div>
      <h1>My Tickets</h1>
      <button onClick={() => Router.push('/tickets/create')}>Sell New Ticket</button>
      <table className="table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Price</th>
            <th>Link</th>
          </tr>
        </thead>
        <tbody>
          {ticketList}
        </tbody>
      </table>
    </div>
  );

}

TicketIndex.getInitialProps = async (context, client) => {
  const { data } = await client.get('http://localhost:3002/api/tickets/myticket');

  return { tickets: data };
}

export default TicketIndex;
