const OrderIndex = ({ orders }) => {
  const orderList = orders.map(orders => {
    return(
      <tr key={orders.id}>
        <td>{orders.ticket.title}</td>
        <td>{orders.ticket.price}</td>
        <td>{orders.status}</td>
      </tr>
    )
  })

  return (
    <div>
      <h1>Tickets</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Ticket</th>
            <th>Price</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {orderList}
        </tbody>
      </table>
    </div>
  );

}

OrderIndex.getInitialProps = async (context, client) => {
  const { data } = await client.get('http://localhost:3003/api/orders');

  return { orders: data };
}

export default OrderIndex;
